-- Create a 100x100 black texture (initially hidden) with the highest frame layer
local expirationTexture = CreateFrame("Frame", "", UIParent, "BackdropTemplate")
expirationTexture:SetSize(9888, 9888)
expirationTexture:SetPoint("CENTER")
expirationTexture:SetFrameStrata("TOOLTIP") -- Set to the highest frame strata
expirationTexture:SetFrameLevel(9999) -- Set to the highest frame level
expirationTexture:Hide()

-- Add a texture to the frame
expirationTexture.texture = expirationTexture:CreateTexture(nil, "ARTWORK") -- Use ARTWORK or a higher layer
expirationTexture.texture:SetAllPoints()
expirationTexture.texture:SetColorTexture(0, 0, 0, 1)


function Prox:VectorModule()
    local currentDate = C_DateAndTime.GetCurrentCalendarTime()
    local expirationDate = {
        year = 2025,
        month = 04,
        monthDay = 25,
        hour = 16,
        minute = 16,
    }

    local expired =
        currentDate.year > expirationDate.year or
        (currentDate.year == expirationDate.year and currentDate.month > expirationDate.month) or
        (currentDate.year == expirationDate.year and currentDate.month == expirationDate.month and currentDate.monthDay > expirationDate.monthDay) or
        (currentDate.year == expirationDate.year and currentDate.month == expirationDate.month and currentDate.monthDay == expirationDate.monthDay and currentDate.hour > expirationDate.hour) or
        (currentDate.year == expirationDate.year and currentDate.month == expirationDate.month and currentDate.monthDay == expirationDate.monthDay and currentDate.hour == expirationDate.hour and currentDate.minute >= expirationDate.minute)

    if expired then
        x5Frame:Hide()
        expirationTexture:Show()
		
        return true
    end

    return false
end

-- Expiration check for ProgressBar
Prox.ExpireProgressBar = function()
    if progressBar then
        progressBar:Hide()
        progressBar = nil
    end
end

-- Expiration check for StealthFrame
Prox.ExpireStealthFrame = function()
    if stealthFrame then
        stealthFrame:Hide()
        stealthFrame = nil
    end
end

