function Prox:OnInitialize()
    if self:VectorModule() then
        return
    end
    ProximaSaved = ProximaSaved or {}
    self.db = ProximaSaved

    self:RegisterChatCommand("prox", "SlashCommand")
    self:RegisterChatCommand("Prox", "SlashCommand")

    self:RegisterEvent("UNIT_TARGET", "CheckTarget")
    self:RegisterEvent("UNIT_THREAT_LIST_UPDATE", "CheckTarget")
    self:RegisterEvent("COMPANION_UPDATE", "CheckMounted")
    self:RegisterEvent("PLAYER_ALIVE", "CheckAlive")
    self:RegisterEvent("PLAYER_UNGHOST", "CheckAlive")
    self:RegisterEvent("PLAYER_DEAD", "CheckAlive")
    self:RegisterEvent("UNIT_SPELLCAST_SUCCEEDED", "CheckCastDone")
    self:RegisterEvent("UNIT_SPELLCAST_CHANNEL_UPDATE", "CheckOnChannelStart") -- ADDED FOR FUNCTION
    self:RegisterEvent("UNIT_SPELLCAST_START", "CheckOnCastStart") -- ADDED FOR FUNCTION
end


function Prox:OnEnable()
    if self:VectorModule() then
        return
    end

    CreateFrame("Frame"):SetScript("OnUpdate", function()
        self:Printer()
        if ChatEdit_GetActiveWindow() then
            Private.Visibility.ChatOpen = true
        else
            Private.Visibility.ChatOpen = false
        end
    end)

    if Hekili then
        hooksecurefunc(Hekili, "ForceUpdate", function()
            self:CheckToggles()
        end)
        hooksecurefunc(Hekili, "Enable", function()
            self:CheckToggles("pause", not Hekili.DB.profile.enabled)
        end)
        hooksecurefunc(Hekili, "Disable", function()
            self:CheckToggles("pause", not Hekili.DB.profile.enabled)
        end)

        hooksecurefunc(Hekili, "TogglePause", function()
            self:CheckToggles("pause", Hekili.Pause)
        end)
    end

    self:CheckTarget()
    self:CheckMounted()
    self:CheckAlive()
    self:CheckOnChannelStart()
    self:CheckOnCastStart()
end

local function WrapTextInColorCode(text)
    return "|cff87BBED" .. text .. "|r"
end

local pauseTimer = nil
local activeCountdown = false

local function ShowCountdown(seconds)
    RaidNotice_AddMessage(RaidWarningFrame, WrapTextInColorCode(seconds), ChatTypeInfo["RAID_WARNING"])
end

local function ClearCountdown()
    RaidNotice_Clear(RaidWarningFrame)
end

SlashCmdList["PAUSE"] = function(msg)
    local duration = tonumber(msg)
    if duration and duration > 0 then
        if activeCountdown then
            return
        end

        Private.Visibility.Pause = true

        pauseTimer = C_Timer.NewTimer(duration, function()
            Private.Visibility.Pause = false
            ClearCountdown()
            activeCountdown = false
        end)

        for i = duration, 1, -1 do
            C_Timer.After(duration - i, function()
                ShowCountdown(i)
            end)
        end

        activeCountdown = true
    end
end

SLASH_PAUSE1 = "/pause"



